import React from 'react';
export const DragComponent = ({ data, sortData, letMeKnow }) => {
    const ds = (event) => {
        event.dataTransfer.setData("Text", event.target.id);
    }
    return (<div className="item-container">
        {data.map((d, idx) =>
            <div id={`item${idx}`}
                className="items"
                key={`item${idx}`}
                onDrag={letMeKnow}
                onDragStart={(e) => {
                    e.preventDefault();
                }}>
                {d.description}
            </div>
        )
        }
    </div >)
}
